import "./HomeScreen.css";
import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";

// Components
import Product from "../components/Product";

//Actions
import { getProducts as listProducts } from "../redux/actions/productActions";

const HomeScreen = () => {
  const dispatch = useDispatch();
  const getProducts = useSelector((state) => state.getProducts);
  const { products, loading, error } = getProducts;

  useEffect(() => {
    dispatch(listProducts());
  }, [dispatch]);

//here

//here

  return (
  <div className="homescreen">

      <div className="homescreen__title">
         <b>Celebrate the joy of giving this Christmas by using offer code : <u> MERRYCHEEZUS20 </u>and save upto 20% on your next order. Terms and Conditions apply.</b>
      </div>


    <div className="slideshow-container">
      <div className="ySlides fade">
          <img src="https://www.seriouseats.com/thmb/38BCl0Vfr3_0QXa_CL_5tJjWZYE=/1088x0/filters:no_upscale():max_bytes(150000):strip_icc():format(webp)/__opt__aboutcom__coeus__resources__content_migration__serious_eats__seriouseats.com__images__20110812-murrays-cheese-goat-03-305b85b491164d11a7389a972becc190.jpg" ></img>
      </div>
      <div className="mySlides fade">
          <img src="https://www.seriouseats.com/thmb/QpWMZtxWgZ3xp12BN_-CDiwKinc=/1088x0/filters:no_upscale():max_bytes(150000):strip_icc():format(webp)/__opt__aboutcom__coeus__resources__content_migration__serious_eats__seriouseats.com__images__20110812-murrays-cheese-feta-01-20d31389ba974175b0142509ba679e13.jpg" ></img>
      </div>
      <div className="mySlides fade">
          <img src="https://www.seriouseats.com/thmb/VJipLV8zrG-EB9PAeeOHEKf7LPM=/1088x0/filters:no_upscale():max_bytes(150000):strip_icc():format(webp)/__opt__aboutcom__coeus__resources__content_migration__serious_eats__seriouseats.com__images__2011__08__20110812-murrays-cheese-taleggio-01-238109aed72e474db3052129d752c114.jpg"></img>
      </div>
      <hr/>
      <br/>
    </div>  


      <div className="homescreen__products">
        {loading ? (
          <h2>Loading...</h2>
        ) : error ? (
          <h2>{error}</h2>
        ) : (
          products.map((product) => (
            <Product
              key={product._id}
              name={product.name}
              description={product.description}
              price={product.price}
              milk={product.milk}
              longdescription={product.longdescription}
              imageUrl={product.imageUrl}
              productId={product._id}
            />
          ))
        )}

      </div>
</div>
  );
};

export default HomeScreen;
